mapper.select:
SELECT u.*, a.`name` as agent_name from user AS u LEFT JOIN agent a ON a.id = u.agent -- 5：客户本身，6：客户的联系人 WHERE 1=1
and u.role_id = #{roleId}
and u.agent = #{agentId}
and u.customer_company = #{customerCompany}
and u.valid = '1'

SELECT u.* FROM user u JOIN role r ON r.id = u.role_id LEFT JOIN agent a ON a.id = u.agent where 1=1
AND (r.id = 1 OR r.id = 2 OR r.id = 3 OR r.id = 4)
AND (r.id = 5 OR r.id = 6)
AND u.account LIKE CONCAT('%','${account}','%' )
AND u.name LIKE CONCAT('%','${name}','%' )
AND u.role_id = ${roleId}
AND u.agent = ${currentUserAgent}
AND u.agent = ${agent}
AND u.agent = 0
AND u.valid = ${valid}
